contracts/LottoMojiCore.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@chainlink/contracts/src/v0.8/vrf/VRFConsumerBaseV2.sol";
import "@chainlink/contracts/src/v0.8/vrf/interfaces/VRFCoordinatorV2Interface.sol";
import "@chainlink/contracts/src/v0.8/automation/AutomationCompatible.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./LottoMojiTickets.sol";
import "./LottoMojiPrizePool.sol";

/**
 * @title LottoMoji Core Contract
 * @dev Contrato principal que maneja la lógica de lotería, sorteos automáticos y VRF
 */
contract LottoMojiCore is 
    VRFConsumerBaseV2, 
    AutomationCompatibleInterface, 
    AccessControl, 
    ReentrancyGuard 
{
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");
    
    // Chainlink VRF
    VRFCoordinatorV2Interface private immutable COORDINATOR;
    uint256 private immutable s_subscriptionId;
    bytes32 private immutable s_keyHash;
    uint32 private constant CALLBACK_GAS_LIMIT = 2500000;
    uint16 private constant REQUEST_CONFIRMATIONS = 3;
    uint32 private constant NUM_WORDS = 1;
    
    // Contratos conectados
    LottoMojiTickets public immutable ticketContract;
    LottoMojiPrizePool public immutable prizePoolContract;
    
    // Array de emojis disponibles (indices de 0 a 24)
    string[25] public EMOJIS = [
        unicode"🌟", unicode"🎈", unicode"🎨", unicode"🌈", unicode"🦄", 
        unicode"🍭", unicode"🎪", unicode"🎠", unicode"🎡", unicode"🎢",
        unicode"🌺", unicode"🦋", unicode"🐬", unicode"🌸", unicode"🍦", 
        unicode"🎵", unicode"🎯", unicode"🌴", unicode"🎩", unicode"🎭",
        unicode"🎁", unicode"🎮", unicode"🚀", unicode"🌍", unicode"🍀"
    ];
    
    // Estado de la lotería
    struct Round {
        uint256 id;
        uint256 startTime;
        uint256 endTime;
        uint256[4] winningNumbers;
        bool isActive;
        bool numbersDrawn;
        uint256 vrfRequestId;
        bool prizesDistributed;
    }
    
    mapping(uint256 => Round) public rounds;
    uint256 public currentRoundId;
    uint256 public lastDrawTime;
    uint256 public constant ROUND_DURATION = 24 hours; // 24 horas por ronda
    
    // VRF requests
    mapping(uint256 => uint256) public vrfRequestToRound;
    
    // Eventos
    event RoundStarted(uint256 indexed roundId, uint256 startTime, uint256 endTime);
    event TicketPurchased(uint256 indexed roundId, address indexed player, uint256 ticketId);
    event NumbersRequested(uint256 indexed roundId, uint256 vrfRequestId);
    event NumbersDrawn(uint256 indexed roundId, uint256[4] winningNumbers);
    event PrizesDistributed(uint256 indexed roundId, uint256 totalWinners);
    event RoundCompleted(uint256 indexed roundId);
    
    modifier onlyActiveRound() {
        require(rounds[currentRoundId].isActive, "No active round");
        _;
    }
    
    constructor(
        address _vrfCoordinator,
        uint256 _subscriptionId,
        bytes32 _keyHash,
        address _ticketContract,
        address _prizePoolContract
    ) VRFConsumerBaseV2(_vrfCoordinator) {
        COORDINATOR = VRFCoordinatorV2Interface(_vrfCoordinator);
        s_subscriptionId = _subscriptionId;
        s_keyHash = _keyHash;
        
        ticketContract = LottoMojiTickets(_ticketContract);
        prizePoolContract = LottoMojiPrizePool(payable(_prizePoolContract));
        
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(OPERATOR_ROLE, msg.sender);
        
        // Iniciar primera ronda
        _startNewRound();
    }
    
    /**
     * @dev Comprar ticket con ETH
     */
    function buyTicketWithETH(uint256[4] memory emojis) 
        external 
        payable 
        onlyActiveRound 
        nonReentrant 
    {
        require(_validateEmojis(emojis), "Invalid emoji selection");
        require(block.timestamp < rounds[currentRoundId].endTime, "Round ended");
        
        // Crear hash del pago
        bytes32 paymentHash = keccak256(
            abi.encodePacked(msg.sender, currentRoundId, block.timestamp, msg.value)
        );
        
        // Procesar pago en prize pool
        prizePoolContract.processETHPayment{value: msg.value}(
            paymentHash, 
            msg.sender, 
            currentRoundId
        );
        
        // Mintear ticket NFT
        uint256 ticketId = ticketContract.mintTicket(
            msg.sender,
            emojis,
            currentRoundId,
            false,
            paymentHash
        );
        
        emit TicketPurchased(currentRoundId, msg.sender, ticketId);
    }
    
    /**
     * @dev Comprar ticket con USDC (requiere previa aprobación)
     */
    function buyTicketWithUSDC(uint256[4] memory emojis) 
        external 
        onlyActiveRound 
        nonReentrant 
    {
        require(_validateEmojis(emojis), "Invalid emoji selection");
        require(block.timestamp < rounds[currentRoundId].endTime, "Round ended");
        
        // Crear hash del pago
        bytes32 paymentHash = keccak256(
            abi.encodePacked(msg.sender, currentRoundId, block.timestamp, "USDC")
        );
        
        // Procesar pago en prize pool
        prizePoolContract.processUSDCPayment(
            paymentHash, 
            msg.sender, 
            currentRoundId
        );
        
        // Mintear ticket NFT
        uint256 ticketId = ticketContract.mintTicket(
            msg.sender,
            emojis,
            currentRoundId,
            false,
            paymentHash
        );
        
        emit TicketPurchased(currentRoundId, msg.sender, ticketId);
    }
    
    /**
     * @dev Chainlink Automation - Verificar si necesita ejecutar sorteo
     */
    function checkUpkeep(bytes calldata)
        external
        view
        override
        returns (bool upkeepNeeded, bytes memory performData)
    {
        Round storage round = rounds[currentRoundId];
        
        // Necesita sorteo si:
        // 1. La ronda está activa
        // 2. Ha pasado el tiempo de finalización
        // 3. No se han sorteado números aún
        upkeepNeeded = round.isActive && 
                       block.timestamp >= round.endTime && 
                       !round.numbersDrawn;
        
        performData = abi.encode(currentRoundId);
    }
    
    /**
     * @dev Chainlink Automation - Ejecutar sorteo automático
     */
    function performUpkeep(bytes calldata performData) external override {
        uint256 roundId = abi.decode(performData, (uint256));
        require(roundId == currentRoundId, "Invalid round");
        require(rounds[roundId].isActive, "Round not active");
        require(block.timestamp >= rounds[roundId].endTime, "Round not ended");
        require(!rounds[roundId].numbersDrawn, "Numbers already drawn");
        
        _requestRandomNumbers(roundId);
    }
    
    /**
     * @dev Solicitar números aleatorios a Chainlink VRF
     */
    function _requestRandomNumbers(uint256 roundId) internal {
        uint256 requestId = COORDINATOR.requestRandomWords(
            s_keyHash,
            uint64(s_subscriptionId),
            REQUEST_CONFIRMATIONS,
            CALLBACK_GAS_LIMIT,
            NUM_WORDS
        );
        
        rounds[roundId].vrfRequestId = requestId;
        vrfRequestToRound[requestId] = roundId;
        
        emit NumbersRequested(roundId, requestId);
    }
    
    /**
     * @dev Callback de Chainlink VRF con números aleatorios
     */
    function fulfillRandomWords(
        uint256 requestId,
        uint256[] memory randomWords
    ) internal override {
        uint256 roundId = vrfRequestToRound[requestId];
        require(roundId > 0, "Invalid request");
        
        // Generar 4 números aleatorios de 0-24 (indices de emojis)
        uint256[4] memory winningNumbers;
        uint256 randomValue = randomWords[0];
        
        for (uint256 i = 0; i < 4; i++) {
            winningNumbers[i] = (randomValue >> (i * 8)) % 25;
        }
        
        rounds[roundId].winningNumbers = winningNumbers;
        rounds[roundId].numbersDrawn = true;
        
        emit NumbersDrawn(roundId, winningNumbers);
        
        // Procesar ganadores y distribuir premios
        _processWinners(roundId);
    }
    
    /**
     * @dev Procesar ganadores y distribuir premios
     */
    function _processWinners(uint256 roundId) internal {
        uint256[] memory validTickets = ticketContract.getRoundValidTickets(roundId);
        uint256[4] memory winningNumbers = rounds[roundId].winningNumbers;
        
        // Arrays para ganadores por categoría
        uint256[] memory firstPrizeTickets = new uint256[](validTickets.length);
        uint256[] memory secondPrizeTickets = new uint256[](validTickets.length);
        uint256[] memory thirdPrizeTickets = new uint256[](validTickets.length);
        uint256[] memory freePrizeTickets = new uint256[](validTickets.length);
        
        uint256 firstCount = 0;
        uint256 secondCount = 0;
        uint256 thirdCount = 0;
        uint256 freeCount = 0;
        
        // Verificar cada ticket
        for (uint256 i = 0; i < validTickets.length; i++) {
            uint256 ticketId = validTickets[i];
            (uint256[4] memory ticketEmojis,,,,,,,) = ticketContract.getTicketInfo(ticketId);
            
            (bool first, bool second, bool third, bool free) = _checkWin(ticketEmojis, winningNumbers);
            
            if (first) {
                firstPrizeTickets[firstCount++] = ticketId;
            } else if (second) {
                secondPrizeTickets[secondCount++] = ticketId;
            } else if (third) {
                thirdPrizeTickets[thirdCount++] = ticketId;
            } else if (free) {
                freePrizeTickets[freeCount++] = ticketId;
            }
        }
        
        // Distribuir premios usando el sistema pari-mutuel
        (
            uint256 ethPerFirst, uint256 ethPerSecond, uint256 ethPerThird,
            uint256 usdcPerFirst, uint256 usdcPerSecond, uint256 usdcPerThird
        ) = prizePoolContract.distributePrizes(roundId, firstCount, secondCount, thirdCount);
        
        // Procesar ganadores
        _processWinnerCategory(firstPrizeTickets, firstCount, ethPerFirst, usdcPerFirst);
        _processWinnerCategory(secondPrizeTickets, secondCount, ethPerSecond, usdcPerSecond);
        _processWinnerCategory(thirdPrizeTickets, thirdCount, ethPerThird, usdcPerThird);
        _processFreeTickets(freePrizeTickets, freeCount, roundId);
        
        rounds[roundId].prizesDistributed = true;
        rounds[roundId].isActive = false;
        
        emit PrizesDistributed(roundId, firstCount + secondCount + thirdCount + freeCount);
        emit RoundCompleted(roundId);
        
        // Iniciar nueva ronda
        _startNewRound();
    }
    
    /**
     * @dev Procesar ganadores de una categoría
     */
    function _processWinnerCategory(
        uint256[] memory tickets,
        uint256 count,
        uint256 ethPerWinner,
        uint256 usdcPerWinner
    ) internal {
        for (uint256 i = 0; i < count; i++) {
            uint256 ticketId = tickets[i];
            
            // Marcar ticket como usado
            ticketContract.useTicket(ticketId);
            
            // Obtener hash de pago para reclamar premio
            (,,,,,,, bytes32 paymentHash) = ticketContract.getTicketInfo(ticketId);
            
            // Transferir premio
            if (ethPerWinner > 0 || usdcPerWinner > 0) {
                prizePoolContract.claimPrize(paymentHash, ethPerWinner, usdcPerWinner);
            }
        }
    }
    
    /**
     * @dev Procesar tickets gratis
     */
    function _processFreeTickets(uint256[] memory tickets, uint256 count, uint256 roundId) internal {
        for (uint256 i = 0; i < count; i++) {
            uint256 ticketId = tickets[i];
            
            // Marcar ticket original como usado
            ticketContract.useTicket(ticketId);
            
            // Obtener dueño del ticket
            (,, address player,,,,,) = ticketContract.getTicketInfo(ticketId);
            
            // Generar ticket gratis para la próxima ronda (nueva ronda que se acaba de crear)
            uint256[4] memory freeEmojis = _generateRandomEmojis();
            uint256 newTicketId = ticketContract.mintTicket(
                player,
                freeEmojis,
                currentRoundId, // Nueva ronda (ya incrementada en _startNewRound)
                true, // Es ticket gratis
                bytes32(0) // Sin hash de pago
            );
            
            // Emitir evento de ticket gratis generado
            emit TicketPurchased(currentRoundId, player, newTicketId);
        }
    }
    
    /**
     * @dev Verificar si un ticket es ganador (lógica de Firebase)
     */
    function _checkWin(
        uint256[4] memory ticketNumbers,
        uint256[4] memory winningNumbers
    ) internal pure returns (bool first, bool second, bool third, bool free) {
        // Verificar coincidencias exactas (mismo emoji en la misma posición)
        uint256 exactMatches = 0;
        for (uint256 i = 0; i < 4; i++) {
            if (ticketNumbers[i] == winningNumbers[i]) {
                exactMatches++;
            }
        }
        
        // Contar emojis que coinciden (cualquier posición)
        uint256 matchCount = 0;
        bool[4] memory used = [false, false, false, false];
        
        for (uint256 i = 0; i < 4; i++) {
            for (uint256 j = 0; j < 4; j++) {
                if (!used[j] && ticketNumbers[i] == winningNumbers[j]) {
                    matchCount++;
                    used[j] = true;
                    break;
                }
            }
        }
        
        // Determinar premio según lógica de Firebase
        first = exactMatches == 4;                    // 4 aciertos en orden exacto
        second = matchCount == 4 && exactMatches != 4; // 4 aciertos en cualquier orden
        third = exactMatches == 3;                     // 3 aciertos en orden exacto
        free = matchCount == 3 && exactMatches != 3;   // 3 aciertos en cualquier orden
    }
    
    /**
     * @dev Iniciar nueva ronda
     */
    function _startNewRound() internal {
        currentRoundId++;
        
        rounds[currentRoundId] = Round({
            id: currentRoundId,
            startTime: block.timestamp,
            endTime: block.timestamp + ROUND_DURATION,
            winningNumbers: [uint256(0), 0, 0, 0],
            isActive: true,
            numbersDrawn: false,
            vrfRequestId: 0,
            prizesDistributed: false
        });
        
        lastDrawTime = block.timestamp;
        
        emit RoundStarted(currentRoundId, block.timestamp, block.timestamp + ROUND_DURATION);
    }
    
    /**
     * @dev Validar selección de emojis
     */
    function _validateEmojis(uint256[4] memory emojis) internal pure returns (bool) {
        for (uint256 i = 0; i < 4; i++) {
            if (emojis[i] >= 25) return false; // Fuera de rango
        }
        return true;
    }
    
    /**
     * @dev Generar emojis aleatorios para tickets gratis
     */
    function _generateRandomEmojis() internal view returns (uint256[4] memory) {
        uint256[4] memory emojis;
        uint256 randomValue = uint256(keccak256(abi.encodePacked(block.timestamp, msg.sender)));
        
        for (uint256 i = 0; i < 4; i++) {
            emojis[i] = (randomValue >> (i * 8)) % 25;
        }
        
        return emojis;
    }
    
    /**
     * @dev Obtener información de la ronda actual
     */
    function getCurrentRoundInfo() external view returns (
        uint256 roundId,
        uint256 startTime,
        uint256 endTime,
        bool isActive,
        bool numbersDrawn,
        uint256[4] memory winningNumbers
    ) {
        Round storage round = rounds[currentRoundId];
        return (
            round.id,
            round.startTime,
            round.endTime,
            round.isActive,
            round.numbersDrawn,
            round.winningNumbers
        );
    }
    
    /**
     * @dev Obtener emoji por índice
     */
    function getEmoji(uint256 index) external view returns (string memory) {
        require(index < 25, "Index out of range");
        return EMOJIS[index];
    }
    
    /**
     * @dev Función manual de sorteo (solo para emergencias)
     */
    function manualDraw(uint256 roundId) external onlyRole(OPERATOR_ROLE) {
        require(rounds[roundId].isActive, "Round not active");
        require(block.timestamp >= rounds[roundId].endTime, "Round not ended");
        require(!rounds[roundId].numbersDrawn, "Numbers already drawn");
        
        _requestRandomNumbers(roundId);
    }
    
    /**
     * @dev Función de emergencia para completar ronda sin VRF
     */
    function emergencyCompleteRound(uint256 roundId, uint256[4] memory winningNumbers) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        require(rounds[roundId].isActive, "Round not active");
        require(!rounds[roundId].numbersDrawn, "Numbers already drawn");
        
        rounds[roundId].winningNumbers = winningNumbers;
        rounds[roundId].numbersDrawn = true;
        
        emit NumbersDrawn(roundId, winningNumbers);
        _processWinners(roundId);
    }
}

contracts/LottoMojiPrizePool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@chainlink/contracts/src/v0.8/shared/interfaces/AggregatorV3Interface.sol";

/**
 * @title LottoMoji Prize Pool Contract
 * @dev Maneja el sistema de pagos, pools de premios y distribución pari-mutuel con reservas
 */
contract LottoMojiPrizePool is ReentrancyGuard, AccessControl {
    bytes32 public constant LOTTERY_ROLE = keccak256("LOTTERY_ROLE");
    bytes32 public constant DEV_ROLE = keccak256("DEV_ROLE");
    
    // Precio fijo del ticket en USD (con 6 decimales como USDC)
    uint256 public constant TICKET_PRICE_USD = 2_000_000; // $2.00 USD
    
    // Distribución de fondos (en porcentajes base 1000)
    uint256 public constant DEV_FEE = 50;           // 5%
    
    // Tercer premio: 5% total = 4% acumulable + 1% reserva
    uint256 public constant THIRD_POOL = 40;       // 4%
    uint256 public constant THIRD_RESERVE = 10;    // 1%
    
    // Segundo premio: 10% total = 8% acumulable + 2% reserva
    uint256 public constant SECOND_POOL = 80;      // 8%
    uint256 public constant SECOND_RESERVE = 20;   // 2%
    
    // Primer premio: 80% total = 64% acumulable + 16% reserva
    uint256 public constant FIRST_POOL = 640;      // 64%
    uint256 public constant FIRST_RESERVE = 160;   // 16%
    
    // Contratos y tokens
    IERC20 public immutable USDC;
    AggregatorV3Interface public immutable ETH_USD_FEED;
    address public immutable devWallet;
    
    // Pools de premios separadas por moneda (ETH y USDC)
    struct PrizePools {
        // Pools acumulables (se usan en cada sorteo)
        uint256 firstPrize;
        uint256 secondPrize;
        uint256 thirdPrize;
        
        // Pools de reserva (se activan cuando hay ganadores)
        uint256 firstReserve;
        uint256 secondReserve;
        uint256 thirdReserve;
    }
    
    PrizePools public ethPools;
    PrizePools public usdcPools;
    
    // Estructura de pago de tickets
    struct TicketPayment {
        address player;
        uint256 ethAmount;
        uint256 usdcAmount;
        uint256 roundId;
        bool prizesClaimed;
    }
    
    mapping(bytes32 => TicketPayment) public ticketPayments;
    mapping(uint256 => uint256) public roundTotalTickets;
    mapping(uint256 => uint256) public roundTotalETH;
    mapping(uint256 => uint256) public roundTotalUSDC;
    
    // Eventos
    event TicketPurchased(
        bytes32 indexed ticketHash,
        address indexed player,
        uint256 roundId,
        uint256 ethAmount,
        uint256 usdcAmount
    );
    
    event PrizesDistributed(
        uint256 indexed roundId,
        uint256 ethFirstPrize,
        uint256 ethSecondPrize,
        uint256 ethThirdPrize,
        uint256 usdcFirstPrize,
        uint256 usdcSecondPrize,
        uint256 usdcThirdPrize
    );
    
    event ReserveUsed(
        uint256 indexed roundId,
        string prizeType,
        uint256 ethReserveUsed,
        uint256 usdcReserveUsed
    );
    
    event PrizeClaimed(
        bytes32 indexed ticketHash,
        address indexed winner,
        uint256 ethAmount,
        uint256 usdcAmount
    );
    
    modifier onlyLottery() {
        require(hasRole(LOTTERY_ROLE, msg.sender), "Only lottery contract");
        _;
    }
    
    constructor(
        address _usdc,
        address _ethUsdFeed,
        address _devWallet,
        address _lotteryContract
    ) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(LOTTERY_ROLE, _lotteryContract);
        _grantRole(DEV_ROLE, _devWallet);
        
        USDC = IERC20(_usdc);
        ETH_USD_FEED = AggregatorV3Interface(_ethUsdFeed);
        devWallet = _devWallet;
    }
    
    /**
     * @dev Obtener precio actual de ETH en USD
     */
    function getETHPrice() public view returns (uint256) {
        (, int256 price, , , ) = ETH_USD_FEED.latestRoundData();
        require(price > 0, "Invalid ETH price");
        return uint256(price) / 100; // Convertir a 6 decimales
    }
    
    /**
     * @dev Calcular cuánto ETH se necesita para $2 USD
     */
    function getETHAmountForTicket() public view returns (uint256) {
        uint256 ethPrice = getETHPrice();
        return (TICKET_PRICE_USD * 1e18) / ethPrice;
    }
    
    /**
     * @dev Procesar pago de ticket con ETH
     */
    function processETHPayment(
        bytes32 ticketHash,
        address player,
        uint256 roundId
    ) external payable onlyLottery nonReentrant {
        uint256 requiredETH = getETHAmountForTicket();
        require(msg.value >= requiredETH, "Insufficient ETH");
        
        // Guardar información del pago
        ticketPayments[ticketHash] = TicketPayment({
            player: player,
            ethAmount: requiredETH,
            usdcAmount: 0,
            roundId: roundId,
            prizesClaimed: false
        });
        
        // Distribuir fondos según nueva lógica
        uint256 devFee = (requiredETH * DEV_FEE) / 1000;
        
        // Tercer premio: 4% acumulable + 1% reserva
        uint256 thirdPool = (requiredETH * THIRD_POOL) / 1000;
        uint256 thirdReserve = (requiredETH * THIRD_RESERVE) / 1000;
        
        // Segundo premio: 8% acumulable + 2% reserva
        uint256 secondPool = (requiredETH * SECOND_POOL) / 1000;
        uint256 secondReserve = (requiredETH * SECOND_RESERVE) / 1000;
        
        // Primer premio: 64% acumulable + 16% reserva
        uint256 firstPool = (requiredETH * FIRST_POOL) / 1000;
        uint256 firstReserve = (requiredETH * FIRST_RESERVE) / 1000;
        
        // Actualizar pools acumulables
        ethPools.thirdPrize += thirdPool;
        ethPools.secondPrize += secondPool;
        ethPools.firstPrize += firstPool;
        
        // Actualizar pools de reserva
        ethPools.thirdReserve += thirdReserve;
        ethPools.secondReserve += secondReserve;
        ethPools.firstReserve += firstReserve;
        
        // Transferir fee de desarrollo
        payable(devWallet).transfer(devFee);
        
        // Devolver exceso si lo hay
        if (msg.value > requiredETH) {
            payable(player).transfer(msg.value - requiredETH);
        }
        
        // Actualizar estadísticas de la ronda
        roundTotalTickets[roundId]++;
        roundTotalETH[roundId] += requiredETH;
        
        emit TicketPurchased(ticketHash, player, roundId, requiredETH, 0);
    }
    
    /**
     * @dev Procesar pago de ticket con USDC
     */
    function processUSDCPayment(
        bytes32 ticketHash,
        address player,
        uint256 roundId
    ) external onlyLottery nonReentrant {
        // Transferir USDC desde el contrato de lotería
        require(
            USDC.transferFrom(msg.sender, address(this), TICKET_PRICE_USD),
            "USDC transfer failed"
        );
        
        // Guardar información del pago
        ticketPayments[ticketHash] = TicketPayment({
            player: player,
            ethAmount: 0,
            usdcAmount: TICKET_PRICE_USD,
            roundId: roundId,
            prizesClaimed: false
        });
        
        // Distribuir fondos según nueva lógica
        uint256 devFee = (TICKET_PRICE_USD * DEV_FEE) / 1000;
        
        // Tercer premio: 4% acumulable + 1% reserva
        uint256 thirdPool = (TICKET_PRICE_USD * THIRD_POOL) / 1000;
        uint256 thirdReserve = (TICKET_PRICE_USD * THIRD_RESERVE) / 1000;
        
        // Segundo premio: 8% acumulable + 2% reserva
        uint256 secondPool = (TICKET_PRICE_USD * SECOND_POOL) / 1000;
        uint256 secondReserve = (TICKET_PRICE_USD * SECOND_RESERVE) / 1000;
        
        // Primer premio: 64% acumulable + 16% reserva
        uint256 firstPool = (TICKET_PRICE_USD * FIRST_POOL) / 1000;
        uint256 firstReserve = (TICKET_PRICE_USD * FIRST_RESERVE) / 1000;
        
        // Actualizar pools acumulables
        usdcPools.thirdPrize += thirdPool;
        usdcPools.secondPrize += secondPool;
        usdcPools.firstPrize += firstPool;
        
        // Actualizar pools de reserva
        usdcPools.thirdReserve += thirdReserve;
        usdcPools.secondReserve += secondReserve;
        usdcPools.firstReserve += firstReserve;
        
        // Transferir fee de desarrollo
        USDC.transfer(devWallet, devFee);
        
        // Actualizar estadísticas de la ronda
        roundTotalTickets[roundId]++;
        roundTotalUSDC[roundId] += TICKET_PRICE_USD;
        
        emit TicketPurchased(ticketHash, player, roundId, 0, TICKET_PRICE_USD);
    }
    
    /**
     * @dev Distribuir premios al final de una ronda (sistema pari-mutuel con reservas)
     */
    function distributePrizes(
        uint256 roundId,
        uint256 firstPrizeWinners,
        uint256 secondPrizeWinners,
        uint256 thirdPrizeWinners
    ) external onlyLottery returns (
        uint256 ethPerFirstWinner,
        uint256 ethPerSecondWinner,
        uint256 ethPerThirdWinner,
        uint256 usdcPerFirstWinner,
        uint256 usdcPerSecondWinner,
        uint256 usdcPerThirdWinner
    ) {
        // PRIMER PREMIO
        if (firstPrizeWinners > 0) {
            // Usar pool acumulable + reserva si es necesario
            uint256 totalETHFirst = ethPools.firstPrize + ethPools.firstReserve;
            uint256 totalUSDCFirst = usdcPools.firstPrize + usdcPools.firstReserve;
            
            ethPerFirstWinner = totalETHFirst / firstPrizeWinners;
            usdcPerFirstWinner = totalUSDCFirst / firstPrizeWinners;
            
            // Registrar uso de reserva si fue necesario
            if (ethPools.firstReserve > 0 || usdcPools.firstReserve > 0) {
                emit ReserveUsed(roundId, "first", ethPools.firstReserve, usdcPools.firstReserve);
            }
            
            // Resetear pools (acumulable y reserva)
            ethPools.firstPrize = 0;
            ethPools.firstReserve = 0;
            usdcPools.firstPrize = 0;
            usdcPools.firstReserve = 0;
        } else {
            // No hay ganadores: pool acumulable sigue creciendo, reserva se mantiene
            // No hacemos nada, los fondos se acumulan para el próximo sorteo
        }
        
        // SEGUNDO PREMIO
        if (secondPrizeWinners > 0) {
            // Usar pool acumulable + reserva si es necesario
            uint256 totalETHSecond = ethPools.secondPrize + ethPools.secondReserve;
            uint256 totalUSDCSecond = usdcPools.secondPrize + usdcPools.secondReserve;
            
            ethPerSecondWinner = totalETHSecond / secondPrizeWinners;
            usdcPerSecondWinner = totalUSDCSecond / secondPrizeWinners;
            
            // Registrar uso de reserva si fue necesario
            if (ethPools.secondReserve > 0 || usdcPools.secondReserve > 0) {
                emit ReserveUsed(roundId, "second", ethPools.secondReserve, usdcPools.secondReserve);
            }
            
            // Resetear pools (acumulable y reserva)
            ethPools.secondPrize = 0;
            ethPools.secondReserve = 0;
            usdcPools.secondPrize = 0;
            usdcPools.secondReserve = 0;
        }
        
        // TERCER PREMIO
        if (thirdPrizeWinners > 0) {
            // Usar pool acumulable + reserva si es necesario
            uint256 totalETHThird = ethPools.thirdPrize + ethPools.thirdReserve;
            uint256 totalUSDCThird = usdcPools.thirdPrize + usdcPools.thirdReserve;
            
            ethPerThirdWinner = totalETHThird / thirdPrizeWinners;
            usdcPerThirdWinner = totalUSDCThird / thirdPrizeWinners;
            
            // Registrar uso de reserva si fue necesario
            if (ethPools.thirdReserve > 0 || usdcPools.thirdReserve > 0) {
                emit ReserveUsed(roundId, "third", ethPools.thirdReserve, usdcPools.thirdReserve);
            }
            
            // Resetear pools (acumulable y reserva)
            ethPools.thirdPrize = 0;
            ethPools.thirdReserve = 0;
            usdcPools.thirdPrize = 0;
            usdcPools.thirdReserve = 0;
        }
        
        emit PrizesDistributed(
            roundId,
            ethPerFirstWinner,
            ethPerSecondWinner,
            ethPerThirdWinner,
            usdcPerFirstWinner,
            usdcPerSecondWinner,
            usdcPerThirdWinner
        );
    }
    
    /**
     * @dev Reclamar premio de un ticket ganador
     */
    function claimPrize(
        bytes32 ticketHash,
        uint256 ethAmount,
        uint256 usdcAmount
    ) external onlyLottery nonReentrant {
        TicketPayment storage payment = ticketPayments[ticketHash];
        require(payment.player != address(0), "Ticket not found");
        require(!payment.prizesClaimed, "Prize already claimed");
        
        payment.prizesClaimed = true;
        
        // Transferir premios
        if (ethAmount > 0) {
            payable(payment.player).transfer(ethAmount);
        }
        
        if (usdcAmount > 0) {
            USDC.transfer(payment.player, usdcAmount);
        }
        
        emit PrizeClaimed(ticketHash, payment.player, ethAmount, usdcAmount);
    }
    
    /**
     * @dev Obtener información de pools actuales (incluyendo reservas)
     */
    function getPoolsInfo() external view returns (
        PrizePools memory ethPoolsInfo,
        PrizePools memory usdcPoolsInfo
    ) {
        return (ethPools, usdcPools);
    }
    
    /**
     * @dev Obtener información detallada de pools
     */
    function getDetailedPoolsInfo() external view returns (
        uint256 ethFirstPrize,
        uint256 ethSecondPrize,
        uint256 ethThirdPrize,
        uint256 ethFirstReserve,
        uint256 ethSecondReserve,
        uint256 ethThirdReserve,
        uint256 usdcFirstPrize,
        uint256 usdcSecondPrize,
        uint256 usdcThirdPrize,
        uint256 usdcFirstReserve,
        uint256 usdcSecondReserve,
        uint256 usdcThirdReserve
    ) {
        return (
            ethPools.firstPrize,
            ethPools.secondPrize,
            ethPools.thirdPrize,
            ethPools.firstReserve,
            ethPools.secondReserve,
            ethPools.thirdReserve,
            usdcPools.firstPrize,
            usdcPools.secondPrize,
            usdcPools.thirdPrize,
            usdcPools.firstReserve,
            usdcPools.secondReserve,
            usdcPools.thirdReserve
        );
    }
    
    /**
     * @dev Verificar si un ticket está pagado
     */
    function isTicketPaid(bytes32 ticketHash) external view returns (bool) {
        return ticketPayments[ticketHash].player != address(0);
    }
    
    /**
     * @dev Obtener estadísticas de una ronda
     */
    function getRoundStats(uint256 roundId) external view returns (
        uint256 totalTickets,
        uint256 totalETH,
        uint256 totalUSDC
    ) {
        return (
            roundTotalTickets[roundId],
            roundTotalETH[roundId],
            roundTotalUSDC[roundId]
        );
    }
    
    /**
     * @dev Función de emergencia para retirar fondos
     */
    function emergencyWithdraw() external onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 ethBalance = address(this).balance;
        uint256 usdcBalance = USDC.balanceOf(address(this));
        
        if (ethBalance > 0) {
            payable(devWallet).transfer(ethBalance);
        }
        
        if (usdcBalance > 0) {
            USDC.transfer(devWallet, usdcBalance);
        }
    }
    
    // Función para recibir ETH
    receive() external payable {}
}

contracts/LottoMojiTickets.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title LottoMoji Soulbound Tickets
 * @dev NFTs soulbound que representan tickets de lotería válidos solo por un día
 */
contract LottoMojiTickets is ERC721, AccessControl, ReentrancyGuard {
    bytes32 public constant LOTTERY_ROLE = keccak256("LOTTERY_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    
    uint256 private _nextTokenId = 1;
    
    // Estructura del ticket
    struct Ticket {
        uint256[4] emojis;      // 4 emojis del ticket
        uint256 roundId;        // ID de la ronda
        address player;         // Dueño del ticket
        bool isUsed;           // Si ya se usó para reclamar premio
        bool isFreeTicket;     // Si es un ticket gratis
        uint256 mintTimestamp; // Cuándo se mintó
        bytes32 paymentHash;   // Hash del pago asociado
    }
    
    mapping(uint256 => Ticket) public tickets;
    mapping(address => uint256[]) public playerTickets;
    mapping(uint256 => uint256[]) public roundTickets;
    mapping(bytes32 => uint256) public paymentToTicket;
    
    // Eventos
    event TicketMinted(
        uint256 indexed tokenId,
        address indexed player,
        uint256 roundId,
        uint256[4] emojis,
        bool isFreeTicket
    );
    
    event TicketUsed(
        uint256 indexed tokenId,
        address indexed player,
        uint256 prizeAmount
    );
    
    event TicketBurned(
        uint256 indexed tokenId,
        address indexed player,
        uint256 roundId
    );
    
    modifier onlyLottery() {
        require(hasRole(LOTTERY_ROLE, msg.sender), "Only lottery contract");
        _;
    }
    
    modifier ticketOwner(uint256 tokenId) {
        require(ownerOf(tokenId) == msg.sender, "Not ticket owner");
        _;
    }
    
    constructor(address _lotteryContract) ERC721("LottoMoji Ticket", "LMTKT") {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(LOTTERY_ROLE, _lotteryContract);
        _grantRole(MINTER_ROLE, _lotteryContract);
    }
    
    /**
     * @dev Mintear un nuevo ticket (solo por contrato de lotería)
     */
    function mintTicket(
        address player,
        uint256[4] memory emojis,
        uint256 roundId,
        bool isFreeTicket,
        bytes32 paymentHash
    ) external onlyRole(MINTER_ROLE) returns (uint256) {
        require(player != address(0), "Invalid player address");
        require(roundId > 0, "Invalid round ID");
        
        uint256 tokenId = _nextTokenId;
        _nextTokenId++;
        
        // Mintear el NFT
        _safeMint(player, tokenId);
        
        // Guardar información del ticket
        tickets[tokenId] = Ticket({
            emojis: emojis,
            roundId: roundId,
            player: player,
            isUsed: false,
            isFreeTicket: isFreeTicket,
            mintTimestamp: block.timestamp,
            paymentHash: paymentHash
        });
        
        // Actualizar mapeos
        playerTickets[player].push(tokenId);
        roundTickets[roundId].push(tokenId);
        if (paymentHash != bytes32(0)) {
            paymentToTicket[paymentHash] = tokenId;
        }
        
        emit TicketMinted(tokenId, player, roundId, emojis, isFreeTicket);
        
        return tokenId;
    }
    
    /**
     * @dev Marcar ticket como usado para reclamar premio
     */
    function useTicket(uint256 tokenId) external onlyLottery {
        require(_ownerOf(tokenId) != address(0), "Ticket does not exist");
        require(!tickets[tokenId].isUsed, "Ticket already used");
        require(isTicketValid(tokenId), "Ticket is not valid");
        
        tickets[tokenId].isUsed = true;
        
        emit TicketUsed(tokenId, tickets[tokenId].player, 0);
    }
    
    /**
     * @dev Verificar si un ticket es válido para la ronda actual
     */
    function isTicketValid(uint256 tokenId) public view returns (bool) {
        if (_ownerOf(tokenId) == address(0)) return false;
        
        Ticket memory ticket = tickets[tokenId];
        
        // Ticket ya usado
        if (ticket.isUsed) return false;
        
        // Verificar que el ticket no haya expirado (válido solo por 24 horas)
        if (block.timestamp > ticket.mintTimestamp + 24 hours) {
            return false;
        }
        
        return true;
    }
    
    /**
     * @dev Obtener todos los tickets de un jugador para una ronda
     */
    function getPlayerTicketsForRound(
        address player, 
        uint256 roundId
    ) external view returns (uint256[] memory validTickets) {
        uint256[] memory allPlayerTickets = playerTickets[player];
        uint256 validCount = 0;
        
        // Contar tickets válidos primero
        for (uint256 i = 0; i < allPlayerTickets.length; i++) {
            uint256 tokenId = allPlayerTickets[i];
            if (tickets[tokenId].roundId == roundId && isTicketValid(tokenId)) {
                validCount++;
            }
        }
        
        // Crear array con el tamaño correcto
        validTickets = new uint256[](validCount);
        uint256 index = 0;
        
        // Llenar array con tickets válidos
        for (uint256 i = 0; i < allPlayerTickets.length; i++) {
            uint256 tokenId = allPlayerTickets[i];
            if (tickets[tokenId].roundId == roundId && isTicketValid(tokenId)) {
                validTickets[index] = tokenId;
                index++;
            }
        }
    }
    
    /**
     * @dev Obtener todos los tickets válidos de una ronda
     */
    function getRoundValidTickets(uint256 roundId) external view returns (uint256[] memory) {
        uint256[] memory allRoundTickets = roundTickets[roundId];
        uint256 validCount = 0;
        
        // Contar tickets válidos
        for (uint256 i = 0; i < allRoundTickets.length; i++) {
            if (isTicketValid(allRoundTickets[i])) {
                validCount++;
            }
        }
        
        // Crear array con tickets válidos
        uint256[] memory validTickets = new uint256[](validCount);
        uint256 index = 0;
        
        for (uint256 i = 0; i < allRoundTickets.length; i++) {
            uint256 tokenId = allRoundTickets[i];
            if (isTicketValid(tokenId)) {
                validTickets[index] = tokenId;
                index++;
            }
        }
        
        return validTickets;
    }
    
    /**
     * @dev Obtener información completa de un ticket
     */
    function getTicketInfo(uint256 tokenId) external view returns (
        uint256[4] memory emojis,
        uint256 roundId,
        address player,
        bool isUsed,
        bool isFreeTicket,
        bool isValid,
        uint256 mintTimestamp,
        bytes32 paymentHash
    ) {
        require(_ownerOf(tokenId) != address(0), "Ticket does not exist");
        
        Ticket memory ticket = tickets[tokenId];
        
        return (
            ticket.emojis,
            ticket.roundId,
            ticket.player,
            ticket.isUsed,
            ticket.isFreeTicket,
            isTicketValid(tokenId),
            ticket.mintTimestamp,
            ticket.paymentHash
        );
    }
    
    /**
     * @dev Quemar tickets expirados de una ronda (limpieza)
     */
    function burnExpiredTickets(uint256 roundId) external onlyLottery {
        uint256[] memory roundTicketsList = roundTickets[roundId];
        
        for (uint256 i = 0; i < roundTicketsList.length; i++) {
            uint256 tokenId = roundTicketsList[i];
            
            // Si el ticket existe y ha expirado, quemarlo
            if (_ownerOf(tokenId) != address(0) && !isTicketValid(tokenId) && !tickets[tokenId].isUsed) {
                address owner = ownerOf(tokenId);
                _burn(tokenId);
                
                emit TicketBurned(tokenId, owner, roundId);
            }
        }
    }
    
    /**
     * @dev Obtener ticket por hash de pago
     */
    function getTicketByPayment(bytes32 paymentHash) external view returns (uint256) {
        return paymentToTicket[paymentHash];
    }
    
    /**
     * @dev Override para hacer los tokens soulbound (no transferibles)
     */
    function _update(
        address to,
        uint256 tokenId,
        address auth
    ) internal override returns (address) {
        address from = _ownerOf(tokenId);
        
        // Permitir mint (from = address(0)) y burn (to = address(0))
        require(
            from == address(0) || to == address(0), 
            "Tickets are soulbound and cannot be transferred"
        );
        
        return super._update(to, tokenId, auth);
    }
    

    
    /**
     * @dev Obtener conteo total de tickets minteados
     */
    function totalSupply() external view returns (uint256) {
        return _nextTokenId;
    }
    
    /**
     * @dev Interface support
     */
    function supportsInterface(bytes4 interfaceId) 
        public 
        view 
        override(ERC721, AccessControl) 
        returns (bool) 
    {
        return super.supportsInterface(interfaceId);
    }
}

